// frontend/src/components/ActivityLibrary.tsx
import React from 'react';
import { useDrag } from 'react-dnd';
import { Activity } from '../types';

interface ActivityLibraryProps {
  activities: Activity[];
}

interface ActivityCardProps {
  activity: Activity;
}

const ActivityCard: React.FC<ActivityCardProps> = ({ activity }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: 'activity',
    item: activity,
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  return (
    <div
      ref={drag as unknown as React.Ref<HTMLDivElement>}  // Fix TypeScript issue
      className="activity-card"
      style={{
        opacity: isDragging ? 0.5 : 1,
        backgroundColor: activity.color,
        cursor: 'move',
        padding: '10px',
        margin: '5px',
        borderRadius: '5px',
        border: '1px solid #ccc',
      }}
    >
      <h4>{activity.name}</h4>
      <p>{activity.duration} min</p>
    </div>
  );
};

const ActivityLibrary: React.FC<ActivityLibraryProps> = ({ activities }) => {
  return (
    <div className="activity-library">
      <h2>Activity Library</h2>
      <div className="activity-list">
        {activities.map((activity) => (
          <ActivityCard key={activity.id} activity={activity} />
        ))}
      </div>
    </div>
  );
};

export default ActivityLibrary;
